package com.netflix.discovery.shared.transport.jersey;
/**
 * @author Tomasz Bak
 */
public abstract class AbstractJerseyEurekaHttpClient implements EurekaHttpClient {
      @Override
      public EurekaHttpResponse getApplications(String... regions) {
          return getApplicationsInternal("apps/", regions);
      }

      @Override
      public EurekaHttpResponse getDelta(String... regions) {
          return getApplicationsInternal("apps/delta", regions);
      }

      @Override
      public EurekaHttpResponse getVip(String vipAddress, String... regions) {
          return getApplicationsInternal("vips/" + vipAddress, regions);
      }

      @Override
      public EurekaHttpResponse getSecureVip(String secureVipAddress, String... regions) {
          return getApplicationsInternal("svips/" + secureVipAddress, regions);
      }

      private EurekaHttpResponse getApplicationsInternal(String urlPath, String[] regions) {
        ClientResponse response = null;
        String regionsParamValue = null;
        try {
            WebResource webResource = jerseyClient.resource(serviceUrl).path(urlPath);
            if (regions != null && regions.length > 0) {
                regionsParamValue = StringUtil.join(regions);
                webResource = webResource.queryParam("regions", regionsParamValue);
            }
            Builder requestBuilder = webResource.getRequestBuilder();
            addExtraHeaders(requestBuilder);
            response = requestBuilder.accept(MediaType.APPLICATION_JSON_TYPE).get(ClientResponse.class);

            Applications applications = null;
            if (response.getStatus() == Status.OK.getStatusCode() && response.hasEntity()) {
                applications = response.getEntity(Applications.class);
            }
            return anEurekaHttpResponse(response.getStatus(), Applications.class)
                    .headers(headersOf(response))
                    .entity(applications)
                    .build();
        } finally {
            if (logger.isDebugEnabled()) {
                logger.debug("Jersey HTTP GET {}/{}?{}; statusCode={}",
                        serviceUrl, urlPath,
                        regionsParamValue == null ? "" : "regions=" + regionsParamValue,
                        response == null ? "N/A" : response.getStatus()
                );
            }
            if (response != null) {
                response.close();
            }
        }
    }      
}

